mapper.update:
UPDATE MOVIE as t1 JOIN (
SELECT #{item.movieNm} as title, #{item.rank} as `rank`
) as t2 ON TRIM(REGEXP_REPLACE(t1.title, '[^[:alnum:][:space:]]+', '')) = TRIM(REGEXP_REPLACE(t2.title, '[^[:alnum:][:space:]]+', '')) SET t1.rank = t2.rank;

mapper.insert:

INSERT INTO SCREEN (CI_NUM, SC_TIME, SC_TIME_END)
VALUES (
#{CI_NUM},
#{SC_TIME},
#{SC_TIME_END}
);

mapper.update:
UPDATE CINEMA
SET
TH_NUM = (SELECT TH_NUM FROM THEATER WHERE TH_REGION = #{TH_REGION}
AND
TH_NAME = #{TH_NAME}),
MOVIE_NUM = (SELECT MOVIE_NUM FROM MOVIE
WHERE TITLE = #{title}),
CI_NUMBER = #{CI_NUMBER}
WHERE CI_NUM =
#{CI_NUM};

mapper.select:
SELECT SUM(payment_total_price)
FROM STOREPAYMENT
WHERE payment_date >=
#{startOfDay}
AND payment_date <
#{endOfDay}
