mapper.select:
SELECT M.MEMBER_NICKNAME , I.INQUIRY_NUM , I.MEMBER_NUM , I.INQUIRY_NAME , I.INQUIRY_DETAIL , I.INQUIRY_PICTURE , I.INQUIRY_DATE , I.INQUIRY_TYPE , DATE_FORMAT(I.INQUIRY_DATE, '%Y-%m-%d') AS INQUIRY_DATE_FORMAT , ROW_NUMBER() OVER(ORDER BY CAST(I.INQUIRY_NUM AS UNSIGNED)) AS RN , IF(A.AS_NUM IS NOT NULL, '답변완료', NULL) AS ISANSWERED , A.AS_NUM FROM INQUIRY I LEFT JOIN MEMBERS M ON I.MEMBER_NUM = M.MEMBER_NUM LEFT JOIN ANSWER A ON I.INQUIRY_NUM = A.IQ_NUM WHERE I.INQUIRY_TYPE LIKE "%T%"
AND I.INQUIRY_NAME LIKE CONCAT('%',#{search},'%')
ORDER BY CAST(I.INQUIRY_NUM AS UNSIGNED) DESC LIMIT #{startRow}, #{pageSize}

	
	SELECT M.MEMBER_ID
		 , I.INQUIRY_NUM
		 , I.INQUIRY_NAME
	  FROM INQUIRY I
	  LEFT JOIN MEMBERS M
		ON I.MEMBER_NUM = M.MEMBER_NUM
	 WHERE CAST(I.INQUIRY_NUM AS UNSIGNED) < #{INQUIRY_NUM}
     and I.MEMBER_NUM = #{MEMBER_NUM}
	 ORDER BY ABS(CAST(I.INQUIRY_NUM AS UNSIGNED) - #{INQUIRY_NUM})
	 LIMIT 1;

	
	SELECT M.MEMBER_ID 
		 , I.INQUIRY_NUM
		 , I.INQUIRY_NAME
	  FROM INQUIRY I
	  LEFT JOIN MEMBERS M
		ON I.MEMBER_NUM = M.MEMBER_NUM
	 WHERE CAST(I.INQUIRY_NUM AS UNSIGNED) > #{INQUIRY_NUM}
     and I.MEMBER_NUM = #{MEMBER_NUM}
	 ORDER BY ABS(CAST(I.INQUIRY_NUM AS UNSIGNED) - #{INQUIRY_NUM})
	 LIMIT 1;

mapper.update:
update INQUIRY
   set inquiry_name = #{inquiry_name}
     , inquiry_detail = #{inquiry_detail}
     , inquiry_picture = #{inquiry_picture}
 where inquiry_num = #{inquiry_num}

mapper.select:
SELECT M.MEMBER_NICKNAME , I.INQUIRY_NUM , I.MEMBER_NUM , I.INQUIRY_NAME , I.INQUIRY_DETAIL , I.INQUIRY_PICTURE , I.INQUIRY_DATE , I.INQUIRY_TYPE , DATE_FORMAT(I.INQUIRY_DATE, '%Y-%m-%d') AS INQUIRY_DATE_FORMAT , ROW_NUMBER() OVER(ORDER BY CAST(I.INQUIRY_NUM AS UNSIGNED)) AS RN , IF(A.AS_NUM IS NOT NULL, '답변완료', NULL) AS ISANSWERED , A.AS_NUM FROM INQUIRY I LEFT JOIN MEMBERS M ON I.MEMBER_NUM = M.MEMBER_NUM LEFT JOIN ANSWER A ON I.INQUIRY_NUM = A.IQ_NUM
WHERE I.INQUIRY_NAME LIKE CONCAT('%',#{search},'%')
ORDER BY CAST(I.INQUIRY_NUM AS UNSIGNED) DESC LIMIT #{startRow}, #{pageSize}
