mapper.select:
SELECT COUNT(*)
FROM REVIEW
where MOVIE_NUM = #{num};

mapper.update:
UPDATE REVIEW
SET RECOMMEND = RECOMMEND + 1
WHERE REVIEW_NUM = #{REVIEW_NUM};

mapper.select:
SELECT RECOMMEND
FROM REVIEW
WHERE REVIEW_NUM = #{REVIEW_NUM};

SELECT if(count(*) = 0, 'true', 'false')
FROM RECOMMEND_CHECK
WHERE REVIEW_NUM = #{REVIEW_NUM}
AND MEMBER_ID = #{MEMBER_ID};

SELECT TH_REGION
FROM CINEMA C
JOIN THEATER T ON C.TH_NUM = T.TH_NUM
WHERE C.MOVIE_NUM = #{MOVIE_NUM}
GROUP BY TH_REGION;
