mapper.select:
SELECT MEM_PASS
FROM MEMBERS
WHERE MEM_ID = #{id}

SELECT TP.TP_NUM, TP.TP_PRICE, TP.TP_SEAT, TP.TP_TYPE, TP.TP_PAYMENT, TP.TP_TIME, M.MEM_ID, S.SC_TIME, S.SC_TIME_END, T.TH_NAME, T.TH_REGION, T.TH_NUMBER, MV.title FROM TICKETPAYMENT TP JOIN MEMBERS M ON TP.MEM_NUM = M.MEM_NUM JOIN SCREEN S ON TP.SC_NUM = S.SC_NUM JOIN CINEMA C ON S.CI_NUM = C.CI_NUM JOIN THEATER T ON C.TH_NUM = T.TH_NUM JOIN MOVIE MV ON C.MOVIE_NUM = MV.MOVIE_NUM WHERE M.MEM_ID = #{memberId}
AND ( TP.TP_NUM LIKE CONCAT('%', #{pageDTO.search}, '%') OR TP.TP_TIME LIKE CONCAT('%', #{pageDTO.search}, '%') OR T.TH_REGION LIKE CONCAT('%', #{pageDTO.search}, '%') OR T.TH_NAME LIKE CONCAT('%', #{pageDTO.search}, '%') OR T.TH_NUMBER LIKE CONCAT('%', #{pageDTO.search}, '%') OR MV.title LIKE CONCAT('%', #{pageDTO.search}, '%') )
ORDER BY TP.TP_NUM DESC LIMIT #{pageDTO.startRow}, #{pageDTO.pageSize}

SELECT SP.SP_NUM, SP.PRODUCT_TOTAL_PRICE, SP.PAYMENT_DATE, S.ST_NAME, S.ST_TYPE, S.ST_CONST FROM STOREPAYMENT SP JOIN STORE S ON SP.ST_NUM = S.ST_NUM JOIN MEMBERS M ON SP.MEM_NUM = M.MEM_NUM WHERE M.MEM_ID = #{memberId}
AND ( SP.SP_NUM LIKE CONCAT('%', #{pageDTO.search}, '%') OR SP.PRODUCT_TOTAL_PRICE LIKE CONCAT('%', #{pageDTO.search}, '%') OR SP.PAYMENT_DATE LIKE CONCAT('%', #{pageDTO.search}, '%') OR S.ST_NAME LIKE CONCAT('%', #{pageDTO.search}, '%') OR S.ST_TYPE LIKE CONCAT('%', #{pageDTO.search}, '%') OR S.ST_CONST LIKE CONCAT('%', #{pageDTO.search}, '%') )
ORDER BY SP.SP_NUM DESC LIMIT #{pageDTO.startRow}, #{pageDTO.pageSize}
