mapper.insert:
INSERT INTO NEWS(NEWS_NUM
				 , NEWS_SECTION
				 , NEWS_NAME
				 , NEWS_CONTENT
				 , NEWS_DATE)
		    VALUES(#{NEWS_NUM}
		         , #{NEWS_SECTION}
		         , #{NEWS_NAME}
		         , #{NEWS_CONTENT}
		         , CAST(#{NEWS_DATE} AS DATE))

mapper.update:
UPDATE NEWS
   SET NEWS_DATE = #{NEWS_DATE}
   	 , NEWS_SECTION = #{NEWS_SECTION}
   	 , NEWS_NAME = #{NEWS_NAME}
     , NEWS_CONTENT = #{NEWS_CONTENT}
 WHERE NEWS_NUM = #{NEWS_NUM}
