mapper.select:

       SELECT * 
         FROM STORETEST 
        WHERE st_type = #{category} 
        ORDER BY st_num DESC;

SELECT * 
  FROM CARTTEST 
 WHERE member_num = #{member_num} AND st_num = #{st_num}

mapper.update:
UPDATE CARTTEST 
   SET cart_quantity = #{cart_quantity}, cart_date = NOW() 
 WHERE cart_num = #{cart_num}
