mapper.select:
SELECT DISTINCT TH_REGION
FROM (
    SELECT TH_REGION
    FROM THEATER
    ORDER BY TH_NUM
) AS T;

mapper.update:
UPDATE MEMBERS
SET FAV_THEATER = #{FAV_THEATER}
WHERE MEM_ID = #{MEM_ID}

mapper.select:
SELECT DISTINCT DATE_FORMAT(S.SC_TIME, '%Y-%m-%d') AS DATE, CASE DAYOFWEEK(S.SC_TIME) WHEN 1 THEN "일" WHEN 2 THEN "월" WHEN 3 THEN "화" WHEN 4 THEN "수" WHEN 5 THEN "목" WHEN 6 THEN "금" WHEN 7 THEN "토" END AS DAYWEEK FROM CINEMA C LEFT OUTER JOIN THEATER T ON C.TH_NUM = T.TH_NUM LEFT OUTER JOIN SCREEN S ON C.CI_NUM = S.CI_NUM LEFT OUTER JOIN MOVIE M ON C.MOVIE_NUM = M.MOVIE_NUM WHERE CURRENT_DATE() <= DATE_FORMAT(S.SC_TIME, '%Y-%m-%d')
AND TH_REGION = #{TH_REGION} AND C.MOVIE_NUM = #{MOVIE_NUM}
AND C.TH_NUM IN (SELECT TH_NUM FROM THEATER WHERE TH_NAME = #{TH_NAME})
ORDER BY DATE_FORMAT(S.SC_TIME, '%Y-%m-%d');
