mapper.select:
SELECT
distinct TH_NAME
distinct TH_NAMEEng as TH_NAME
FROM THEATER T LEFT JOIN CINEMA C ON T.TH_NUM = C.TH_NUM LEFT JOIN MOVIE M ON C.MOVIE_NUM = M.MOVIE_NUM LEFT JOIN SCREEN S ON S.CI_NUM = C.CI_NUM WHERE 1=1 AND TH_REGION = #{region}
AND title = #{selectMovieTitle}
AND DATE(S.SC_TIME) = #{fullDate}

SELECT THEATER.TH_NUMBER, MSEAT.SE_COUNT, COUNT(MSEAT.SE_TYPE) AS NOW_COUNT FROM MSEAT JOIN THEATER ON MSEAT.TH_NUM = THEATER.TH_NUM WHERE THEATER.TH_REGION = #{region} AND THEATER.TH_NAME = #{selectTheaterName} AND THEATER.TH_NUMBER IN
#{item}
AND MSEAT.SE_TYPE = 'empty' GROUP BY THEATER.TH_NUMBER, MSEAT.SE_COUNT;

SELECT
DISTINCT title
DISTINCT titleEng as title
FROM THEATER T LEFT JOIN CINEMA C ON T.TH_NUM = C.TH_NUM LEFT JOIN MOVIE M ON C.MOVIE_NUM = M.MOVIE_NUM LEFT JOIN SCREEN S ON S.CI_NUM = C.CI_NUM WHERE 1=1
AND T.TH_REGION = #{region} AND T.TH_NAME = #{selectTheaterName}
AND DATE(S.SC_TIME) = #{fullDate}

SELECT DISTINCT SC_TIME FROM THEATER T LEFT JOIN CINEMA C ON T.TH_NUM = C.TH_NUM LEFT JOIN MOVIE M ON C.MOVIE_NUM = M.MOVIE_NUM LEFT JOIN SCREEN S ON S.CI_NUM = C.CI_NUM WHERE 1=1
AND T.TH_REGION = #{region} AND T.TH_NAME = #{selectTheaterName}
AND title = #{selectMovieTitle}

mapper.insert:
INSERT INTO
SEATPAYMENT (
SC_NUM,
TP_NUM,
SP_SEAT
)
VALUES (
(
SELECT SC_NUM
FROM SCREEN
WHERE CI_NUM = (
SELECT CI_NUM
FROM CINEMA
WHERE TH_NUM = (
SELECT TH_NUM
FROM THEATER
WHERE TH_REGION = #{th_region}
AND TH_NAME = #{th_name}
AND
TH_NUMBER = #{th_number}
)
AND MOVIE_NUM = (
SELECT MOVIE_NUM
FROM MOVIE
WHERE TITLE = #{movietitle}
)
)
AND SC_TIME = CONCAT(#{fulldate}, ' ',
#{sctime})
),
#{tpNum},
#{tpSeat}
)

INSERT INTO TICKETPAYMENT (MEM_NUM, TP_PAYMENT, SC_NUM, TP_PRICE,
TP_TYPE, TP_SEAT)
VALUES (
#{member_num},  
'F',  
(
SELECT SC_NUM
FROM SCREEN
WHERE CI_NUM = (
SELECT CI_NUM
FROM CINEMA
WHERE TH_NUM = (
SELECT TH_NUM
FROM THEATER
WHERE TH_REGION =
#{th_region}
AND TH_NAME = #{th_name}
AND TH_NUMBER = #{th_number}
)
AND
MOVIE_NUM = (
SELECT MOVIE_NUM
FROM MOVIE
WHERE TITLE = #{movietitle}
)
)
AND SC_TIME = CONCAT(#{fulldate}, ' ', #{sctime})
),
#{tpPrice},  
#{tpType},    
#{tpSeat}
)

