mapper.delete:
DELETE FROM TICKETPAYMENT WHERE TP_NUM IN
#{tpNum}
AND TP_PAYMENT = 'F'

DELETE S FROM SEATPAYMENT S LEFT JOIN TICKETPAYMENT T ON S.TP_NUM = T.TP_NUM WHERE S.TP_NUM IN
#{tpNum}
AND T.TP_PAYMENT = 'F'

